mapper.update:
update pd_diary



where
user_id = #{userId} and diary_id = #{diaryId}

mapper.delete:
delete
from pd_diary
where user_id = #{userId}
  and diary_id = #{diaryId}

mapper.select:
from pd_diary
user_id = #{userId}
and concat(diary_title,diary_weather,diary_mood,diary_event,diary_location,diary_create_time,diary_update_time,detail_content) like concat(concat('%',#{global}),'%')
order by diary_update_time limit #{start},#{size}
