mapper.update:
update private_diary.pd_diary




where
user_id = #{userId} and diary_id = #{diaryId}

mapper.delete:
delete
from private_diary.pd_diary
where user_id = #{userId}
  and diary_id = #{diaryId}

mapper.select:
from private_diary.pd_diary
user_id = #{userId}
and concat(diary_title,diary_weather,diary_mood,diary_event,diary_location,diary_create_time,diary_update_time,diary_content) like concat(concat('%',#{global}),'%')
order by diary_update_time limit #{start},#{size}

select count(diary_id)
from private_diary.pd_diary
where diary_create_time >= #{beginDate}
  and diary_create_time <= #{endDate}
  and user_id = #{userId}
